games = Game.create!(title: "Overwatch", genre: "FPS", description: "Overwatch is a 2016 team-based multiplayer first-person shooter game developed and published by Blizzard Entertainment. Described as a hero shooter, Overwatch assigns players into two teams of six, with each player selecting from a large roster of characters, known as heroes, with unique abilities.", image_url: "https://upload.wikimedia.org/wikipedia/en/thumb/5/51/Overwatch_cover_art.jpg/220px-Overwatch_cover_art.jpg")
games = Game.create!(title: "Valorant", genre: "FPS", description: "Valorant is a tactical shooting game involving two teams with 5 players in each team. Every player can sign in and play remotely from anywhere in the world. Every game has 25 rounds and the team that wins 13 of them first wins the game. Players can choose their in-game characters called agents at the start of the game.", image_url: "https://images.livemint.com/img/2020/06/03/1600x900/Valorant_1591218052835_1591218061187.jpg")
games = Game.create!(title: "Minecraft", genre: "Sandbox", description: "Minecraft is a 3-D computer game where players can build anything. The game which has been described as like an 'online Lego' involves building blocks and creating structures across different environments and terrains. Set in a virtual world the game involves resource gathering, crafting items, building, and combat.", image_url: "https://upload.wikimedia.org/wikipedia/en/5/51/Minecraft_cover.png")
games = Game.create!(title: "League of Legends", genre: "MOBA", description: "League of Legends is a team-based strategy game where two teams of five powerful champions face off to destroy the other's base. Choose from over 140 champions to make epic plays, secure kills, and take down towers as you battle your way to victory.", image_url: "https://www.gamingscan.com/wp-content/uploads/2020/10/League-Of-Legends-System-Requirements.jpg")
games = Game.create!(title: "Apex Legends", genre: "Battle Royal", description: "Apex Legends is an online multiplayer battle royale game featuring squads of three players using pre-made characters with distinctive abilities, called Legends, similar to those of hero shooters. Alternate modes have been introduced allowing for single and for two-player squads since the game's release.", image_url: "https://upload.wikimedia.org/wikipedia/en/d/db/Apex_legends_cover.jpg")
games = Game.create!(title: "Counter-Strike: Global Offensive", genre: "Tactical Shooter", description: "Counter-Strike: Global Offensive (CS:GO) is a round-based, 5v5 tactical FPS with an Attackers vs. Defenders setup and no respawns, meaning if a player is eliminated they will not respawn until the next round. ", image_url: "https://m.media-amazon.com/images/M/MV5BYzJjMmM4MDktNTI0OS00ZDNkLTk4YmItMjk0Yjc5NDA5YWM4XkEyXkFqcGdeQXVyNjc0NTEzOTA@._V1_.jpg")
games = Game.create!(title: "Grand Theft Auto V", genre: "Open World", description: "Grand Theft Auto V is an action-adventure game played from either a third-person or first-person perspective. Players complete missions—linear scenarios with set objectives—to progress through the story. Outside of the missions, players may freely roam the open world.", image_url: "https://upload.wikimedia.org/wikipedia/en/a/a5/Grand_Theft_Auto_V.png")
games = Game.create!(title: "Rust", genre: "Survival", description: "As a multiplayer-only video game, Rust pits players against each other in a harsh, open world environment with the sole goal of survival. Animals, such as wolves and bears, act as a looming threat, but the primary danger comes from other players.", image_url: "https://compass-ssl.xbox.com/assets/3b/98/3b98b3c7-aea8-4dbb-a602-f070170c472f.jpg?n=Rust_GLP-Page-Hero-1084_1920x1080.jpg")
games = Game.create!(title: "Rocket League", genre: "Sports", description: "Rocket League is a high-powered hybrid of arcade-style soccer and vehicular mayhem with easy-to-understand controls and fluid, physics-driven competition.", image_url: "https://theme.zdassets.com/theme_assets/1094427/189dce017fb19e3ca1b94b2095d519cc514df22c.jpg")
games = Game.create!(title: "Elden Ring", genre: "Open World", description: "Elden Ring is an action role-playing game played in a third-person perspective with gameplay focusing on combat and exploration; it features elements similar to those found in other games developed by FromSoftware, such as the Souls series, Bloodborne, and Sekiro: Shadows Die Twice.", image_url: "https://storage.googleapis.com/gamebyte/2021/06/feecce7d-eldenring.jpg")
games = Game.create!(title: "Lost Ark", genre: "MMO", description: "Lost Ark is a 3D fantasy MMORPG with action RPG style combat and a huge world to explore. Set sail on your own ship to discover new regions and battle powerful bosses with a raid party. Lost Ark offers a feature-rich gaming experience combined with stunning visuals and skill effects.", image_url: "https://images.ctfassets.net/umhrp0op95v1/3uKxpqws73m0VRKwmOk3WA/2c14d34e4080b0d226d002bc2cccade6/Lost_Ark_Key_Art_1200x630.jpg")